) {
    const matched_product_href =
        DOSE_SPOT_GENERIC_DRUG_NAME_PAIRS[generic_drug_name] || null;

    const supabase = createSupabaseServerComponentClient();

    const { data, error } = await supabase
        .from('orders')
        .select(
            `
        id,
        external_tracking_metadata,
        order_status,
        product_href,
        assigned_pharmacy,
        subscription_type,
        price_id,
        customer_uid,
        assigned_provider,
        address_line1,
        address_line2,
