export async function getCombinedOrder(
    user_id: string | false | undefined
): Promise<BaseOrder | null> {
    if (!user_id) {
        return null;
    }

    var finalOrder;

    const orderData = await getOrderForProduct(
        PRODUCT_HREF.WEIGHT_LOSS,
        user_id
