    if (!orderData) {
        const combinedOrder = await getCombinedWeightlossOrderForUser(user_id);
        if (!combinedOrder) {
            console.error('Could not get combined order for user', user_id);
        }
        finalOrder = combinedOrder;
    } else {
        finalOrder = orderData;
    }

    return finalOrder;
}

