export async function checkIfTaskAlreadyExists(
    taskType: TaskType,
    taskData: StatusTagObject | TaskOrderObject | TaskRenewalObject
): Promise<boolean> {
    const supabase = createSupabaseServiceClient();

    let orderId: string;
