export function convertToCSV(
    data: any[],
    excludeColumns: string[] = []
): string {
    if (!data || data.length === 0) return '';

    const allHeaders = Object.keys(data[0]);
    const headers = allHeaders.filter(
        (header) => !excludeColumns.includes(header)
    );

    const csvRows = [
        headers.join(','),
        ...data.map((row) =>
            headers.map((header) => JSON.stringify(row[header] ?? '')).join(',')
        ),
    ];

    return csvRows.join('\n');
}

export function downloadCSV(
    data: any[],
    filename: string,
    excludeColumns: string[] = []
): void {
    const csvContent = convertToCSV(data, excludeColumns);
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    if (link.download !== undefined) {
        const url = URL.createObjectURL(blob);
        link.setAttribute('href', url);
        link.setAttribute('download', filename);
        link.style.visibility = 'hidden';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    }
}

export function formatE164(phoneNumber: string): string {
    // Define the country code, in this case for the United States
    const countryCode = '+1';

    // Remove all non-numeric characters from the phone number
    const cleanedNumber = phoneNumber.replace(/\D/g, '');

    // Concatenate the country code with the cleaned number
    const e164Number = countryCode + cleanedNumber;

    return e164Number;
}

getDateHourDifference = (dateA: Date, dateB: Date): number => {
    if(!dateA  || !dateB){
        return 0
    }
    const diffInMs = Math.abs(Number(dateB) - Number(dateA));
    return Math.round(diffInMs / (1000 * 60 * 60));
}

getDateDayDifference = (dateA: Date, dateB: string): number | null => {
    if(!dateA  || !dateB){
        return 0
    }
    const dateBDate = new Date(dateB);

    // Calculate the difference in milliseconds
    const diffInMs = Math.abs(dateBDate.getTime() - dateA.getTime());

    // Convert milliseconds to days
    const totalDays = diffInMs / (1000 * 60 * 60 * 24);

    return totalDays;
}

getDateMonthDifference = (dateA: Date, dateB: string): number => {
    const dateBMillis = dateB;
    const dateBDate = new Date(dateBMillis);

      const diffInMs = Math.abs(dateBDate.getTime() - dateA.getTime());

      const avgDaysInMonth = 30.44; // Approximate average number of days in a month
      const totalDays = diffInMs / (1000 * 60 * 60 * 24); // Convert milliseconds to days
      const monthDifference = Math.round(totalDays / avgDaysInMonth);

      return monthDifference;
}

addDeltaToDate = (
    dateA: Date,
    delta: number,
    mode: string,
): Date => {
    const result = new Date(dateA);


    if (mode == 'w') {
        result.setDate(result.getDate() + delta * 7);
    } else if (mode == 'd') {
        result.setDate(result.getDate() + delta);
    }
    return result;
}

convertEpochToDate = (epoch: number): Date => {
    return new Date(epoch * 1000);
}

convertDateToEpoch = (date: Date): number => {
    return Math.floor(date.getTime() / 1000); // Convert milliseconds to seconds
}

