            let currentRoute = pathArray[pathArray.length - 2] === INTAKE_ROUTE_V3.QUESTIONS
                ? INTAKE_ROUTE_V3.QUESTIONS
                : pathArray[pathArray.length - 1];

            // Check if we're in the questions flow
            if (currentRoute === INTAKE_ROUTE_V3.QUESTIONS || !isNaN(parseInt(currentRoute))) {
