            const v3RouteVariants = [
                'pre-id-v3-ap',
                INTAKE_ROUTE_V3.PRE_ID_VERIFICATION_AP,
                'id-verification-v3',
                INTAKE_ROUTE_V3.ID_VERIFICATION,
