                INTAKE_ROUTE_V3.SHIPPING_INFORMATION,
                'new-checkout-v3',
                INTAKE_ROUTE_V3.NEW_CHECKOUT
            ];

            // Check if current route matches any variant
            const matchingVariant = v3RouteVariants.find(variant => 
                variant === currentRoute
            );

            if (matchingVariant) {
                const routeIndex = route_array.indexOf(matchingVariant);
                if (routeIndex !== -1) {
                    next_route_string = route_array[routeIndex + 1];
                    break;
                }
            }

            // Try to find the route in the array
            const indexFound = route_array.indexOf(currentRoute);
            if (indexFound === -1) {
                console.error('Route not found in the route array:', currentRoute);
                next_route_string = route_array[0];
                break;
            }

            next_route_string = route_array[indexFound + 1];
            break;
        }


        default: {
            const current_version =
                LATEST_INTAKE_VERSIONS.standard.latest_version;
            console.log('current_version', current_version);

            if (first) {
                next_route_string =
                    STANDARD_INTAKE_ROUTES[current_version].route_array[0];
