    return next_route_string;
}

export function getCurrentIntakeProgressBySection(
    currentPath: string,
    product_href: string
) {
    let routesArray;
    // console.log(`logging current path ${currentPath}`);
    const currentPathArray = currentPath.split('/');

    const vwo_test_ids: string[] =
        typeof window !== 'undefined'
            ? JSON.parse(localStorage.getItem('vwo_ids') || '[]')
            : [];

    let currentRoute = '';
    if (currentPathArray.includes(INTAKE_ROUTE_V3.PRE_QUESTIONS)) {
        currentRoute = INTAKE_ROUTE_V3.PRE_QUESTIONS;
    } else if (
        product_href === PRODUCT_HREF.WEIGHT_LOSS ||
        vwo_test_ids.includes(AB_TESTS_IDS.WL_HERS_FUNNEL) ||
        product_href === PRODUCT_HREF.SEMAGLUTIDE ||
        product_href === PRODUCT_HREF.TIRZEPATIDE ||
        product_href === PRODUCT_HREF.B12_INJECTION ||
        product_href === PRODUCT_HREF.NAD_INJECTION ||
        product_href === PRODUCT_HREF.SERMORELIN
    ) {
        currentRoute =
            currentPathArray[currentPathArray.length - 2] ===
            INTAKE_ROUTE_V3.QUESTIONS
                ? INTAKE_ROUTE_V3.QUESTIONS
                : currentPathArray[currentPathArray.length - 1];
    } else {
        currentRoute =
            currentPathArray[currentPathArray.length - 2] ===
            INTAKE_ROUTE.QUESTIONS
                ? INTAKE_ROUTE.QUESTIONS
                : currentPathArray[currentPathArray.length - 1];
    }

    if (currentPath.includes('ed-selection')) {
        currentRoute = INTAKE_ROUTE.ED_SELECTION;
        //since the ed-global architecture appends more url components to the currentPath array at ed-selection, the [currentPathArray.length - 1] logic won't work
    }

    switch (product_href) {
        case PRODUCT_HREF.METFORMIN:
            routesArray = METFORMIN_INTAKE_ROUTES[1].route_array;
            break;
        case PRODUCT_HREF.B12_INJECTION:
            routesArray = B12_INTAKE_ROUTES[2].route_array;
            break;
        case PRODUCT_HREF.TRETINOIN:
            routesArray = SKINCARE_INTAKE_ROUTES[2].route_array;
            break;
        case PRODUCT_HREF.NAD_NASAL_SPRAY:
            routesArray = NAD_NASAL_SPRAY_INTAKE_ROUTES[1].route_array;
            break;
        case PRODUCT_HREF.ED_GLOBAL:
            routesArray = ED_GLOBAL_INTAKE_ROUTES[1].route_array;
            break;
        case PRODUCT_HREF.PEAK_CHEWS:
            routesArray = ED_PRODUCT_INTAKE_ROUTES[1].route_array;
            break;
        case PRODUCT_HREF.X_CHEWS:
        case PRODUCT_HREF.X_MELTS:
            routesArray = ED_X_PRODUCTS_INTAKE_ROUTES[1].route_array;
            break;
        case PRODUCT_HREF.RUSH_MELTS:
            routesArray = RUSH_MELTS_INTAKE_ROUTES[1].route_array;
            break;
        case PRODUCT_HREF.SEMAGLUTIDE:
            const currentVersion =
                LATEST_INTAKE_VERSIONS.semaglutide.latest_version;
            routesArray = getRouteArrayForTest(
                SEMAGLUTIDE_ROUTES[currentVersion],
                vwo_test_ids
            );
            break;
        case PRODUCT_HREF.TIRZEPATIDE:
            const current_version =
                LATEST_INTAKE_VERSIONS.tirzepatide.latest_version;

            routesArray = getRouteArrayForTest(
                TIRZEPATIDE_ROUTES[current_version],
                vwo_test_ids
            );
            break;
        case PRODUCT_HREF.WEIGHT_LOSS:
            const current_version_WL =
                LATEST_INTAKE_VERSIONS.combined_weight_loss.latest_version;

            routesArray = getRouteArrayForTest(
                COMBINED_WEIGHT_LOSS_ROUTES[current_version_WL],
                vwo_test_ids
            );
            break;
        case PRODUCT_HREF.NAD_INJECTION:
            routesArray = NAD_INTAKE_ROUTES[2].route_array;
            break;
        case PRODUCT_HREF.SERMORELIN:
            routesArray = SERMORELIN_ROUTES[1].route_array;
            break;
        default:
            routesArray = NAD_NASAL_SPRAY_INTAKE_ROUTES[1].route_array;
            break;
    }

    if (
        DO_NOT_SHOW_PROGRESS_BAR_ROUTES.includes(currentRoute as INTAKE_ROUTE)
    ) {
        return {
            progress: -1,
            currentSection: -1,
        };
    }

    let calculated_progress: number = 0;
    let calculated_section: number = 0;

    const questions_index_number = routesArray.indexOf(
        product_href === PRODUCT_HREF.WEIGHT_LOSS ||
            vwo_test_ids.includes(AB_TESTS_IDS.WL_HERS_FUNNEL) ||
            product_href === PRODUCT_HREF.SEMAGLUTIDE ||
            product_href === PRODUCT_HREF.TIRZEPATIDE ||
            product_href === PRODUCT_HREF.B12_INJECTION ||
            product_href === PRODUCT_HREF.NAD_INJECTION ||
            product_href === PRODUCT_HREF.SERMORELIN
            ? INTAKE_ROUTE_V3.QUESTIONS
            : INTAKE_ROUTE.QUESTIONS
    );

    let current_index_number = routesArray.indexOf(currentRoute);
    if (currentPath.includes('ed-ind-quantity-selection')) {
        current_index_number = 11; //this route is not in the ED_PRODUCT_INTAKE_ROUTES, because it's not always shown
        //we need to hardcode this value for the intake progress bar to show anything on this screen
    }

    console.log('CURRENT INDEX', current_index_number);
    console.log('QUESTION INDEX', questions_index_number);

    if (current_index_number < questions_index_number) {
        calculated_section = 0;
        if (currentRoute === INTAKE_ROUTE_V3.PRE_QUESTIONS) {
            const initialProgress = Math.floor(
                (100 / questions_index_number) * (current_index_number + 1)
            );
            const current_question_id = parseInt(
                currentPathArray[currentPathArray.length - 1]
            );

            const current_question_id_index =
                HERS_PRE_QUESTIONS_ID_LIST.indexOf(current_question_id);

            calculated_progress = Math.floor(
                (current_question_id_index /
                    HERS_PRE_QUESTIONS_ID_LIST.length) *
                    100
            );
            const finalProgress = initialProgress + calculated_progress / 11;
            calculated_progress = finalProgress;
        } else {
            calculated_progress = Math.floor(
                (100 / questions_index_number) * (current_index_number + 1)
            );
        }
    } else if (current_index_number > questions_index_number) {
        calculated_section = 2;
        calculated_progress = Math.floor(
            100 -
                (100 / (routesArray.length - questions_index_number)) *
                    (routesArray.length - current_index_number)
        );
    } else {
        calculated_section = 1;

        let question_id_array: number[];
        switch (product_href) {
            case PRODUCT_HREF.METFORMIN:
                question_id_array = METFORMIN_QUESTION_ID_LIST;
                break;
            case PRODUCT_HREF.B12_INJECTION:
                question_id_array = B12_QUESTION_ID_LIST;
                break;
            case PRODUCT_HREF.NAD_NASAL_SPRAY:
                question_id_array = NAD_NASAL_SPRAY_QUESTION_ID_LIST;
                break;
            case PRODUCT_HREF.NAD_INJECTION:
                question_id_array = NAD_INJECTION_QUESTION_ID_LIST;
                break;
            case PRODUCT_HREF.ED_GLOBAL:
                question_id_array = ED_GLOBAL_QUESTION_ID_LIST;
                break;
            case PRODUCT_HREF.X_CHEWS:
            case PRODUCT_HREF.X_MELTS:
            case PRODUCT_HREF.PEAK_CHEWS:
            case PRODUCT_HREF.RUSH_MELTS:
                question_id_array = ED_X_QUESTION_ID_LIST;
                break;
            case PRODUCT_HREF.TRETINOIN:
                question_id_array = SKINCARE_QUESTION_ID_LIST;
                break;
            case PRODUCT_HREF.SEMAGLUTIDE:
            case PRODUCT_HREF.TIRZEPATIDE:
                if (vwo_test_ids.includes(AB_TESTS_IDS.WL_HERS_FUNNEL)) {
                    question_id_array = HERS_SEMAGLUTIDE_QUESTION_ID_LIST;
                } else if (
                    vwo_test_ids.includes(AB_TESTS_IDS.WL_NEW_SCREEN_TEST)
                ) {
                    question_id_array = WL_NEW_SCREENS_QUESTION_ID_LIST;
                } else if (
                    vwo_test_ids.includes(AB_TESTS_IDS.COMP_COMPARE)
                ) {
                    question_id_array = SEM_COMPETITOR_COMPARISON_QUESTION_ID_LIST;
                } 
                else {
                    question_id_array = WL_QUESTION_ID_LIST;
                }
                break;
            case PRODUCT_HREF.WEIGHT_LOSS:
                if (vwo_test_ids.includes(AB_TESTS_IDS.WL_NEW_SCREEN_TEST)) {
                    question_id_array =
                        WEIGHT_LOSS_NEW_SCREENS_QUESTION_ID_LIST;
                } else {
                    question_id_array = WEIGHT_LOSS_QUESTION_ID_LIST;
                }
                break;
            case PRODUCT_HREF.SERMORELIN:
                question_id_array = SERMORELIN_QUESTION_ID_LIST;
                break;
            default:
                question_id_array = SKINCARE_QUESTION_ID_LIST;
                break;
        }

        const current_question_id = parseInt(
            currentPathArray[currentPathArray.length - 1]
        );

        const current_question_id_index =
            question_id_array.indexOf(current_question_id);

        calculated_progress = Math.floor(
            (current_question_id_index / question_id_array.length) * 100
        );
    }

    /**
     * For calculated section the options are 0, 1, and 2.
     * 0 represents section prior to questions
     * 1 represents during the quesitons
     * 2 represents section after questions.
     */
    return {
        progress: calculated_progress,
        currentSection: calculated_section,
    };
}

export function getRouteArrayForTest(
    routeObject: RouteObject,
    test_ids: string[]
): string[] {
    // If `test_ids` is not provided or empty, return the default route_array
    if (!test_ids || test_ids.length === 0) {
        return routeObject.route_array;
    }

    // Find all ab_tests that match any of the test_ids
    const matchingTests = routeObject.ab_tests.filter((abTest) =>
        test_ids.includes(abTest.id)
    );

    if (matchingTests.length > 1) {
        // More than one matching test  throw an error or handle as needed
        // If we're dealing with this case, priority code needs to be added here

        return routeObject.route_array;
    }

    // If exactly one match, return its route_array
    if (matchingTests.length === 1) {
        return matchingTests[0].route_array;
    }

    // If no matches, return the default route_array
    return routeObject.route_array;
}

isTransitionScreen = (question: any) => {
    if (question && question.custom_name) {
        return TRANSITION_SCREEN_NAMES.includes(question.custom_name);
    }
    return false;
}

export function usedAllRefills(subscription: PrescriptionSubscription) {
    const href = subscription.product_href as PRODUCT_HREF;
    const type = subscription.subscription_type as SubscriptionCadency;
    const mapping = MAX_REFILLS_MAPPING[href];
    const maxRefills = mapping ? mapping[type] : null;

    // If the lookup fails throw an error
    if (!maxRefills) {
        const msg = `Max refills lookup failed for ${subscription.product_href} -> ${subscription.subscription_type}`;
        console.error(msg);
        throw Error(msg);
    }

    return subscription.since_last_checkup >= maxRefills.max_refills;
}

export async function getReviewStatusTagForRenewalOrder(
    renewal_order_id: string,
): Promise<StatusTag> {
    const months = await getMonthsIntoRenewalOrderSubscription(
        renewal_order_id,
    );

    if (!months) {
        return StatusTag.Review;
    } else if (months === 1 || months === 2) {
        return StatusTag.ReviewNoPrescribe;
    } else if (months >= 3) {
        return StatusTag.FinalReview;
    }
    return StatusTag.Review;
}

sendConvertEvent = async (
    event_type: string,
    event_id: string,
    payload: any,
    values: any = {},
    conversionPayload: any,
) => {
    const customData = {
        value: payload.value,
        currency: payload.currency,
    };

    try {
        const response = await axios.post(
            `https://graph.facebook.com/v19.0/${process.env.NEXT_PUBLIC_PIXEL_ID}/events?access_token=${process.env.FBACCESSKEY}`,
            {
                data: [
                    {
                        ...conversionPayload,
                        ...(event_type === 'Purchase' && {
                            custom_data: customData,
                        }),
                    },
                ],
                ...(process.env.NEXT_PUBLIC_ENVIRONMENT === 'dev' && {
                    test_event_code: process.env.NEXT_PUBLIC_CONVERSION_TEST_ID,
                }),
            },
        );
    } catch (error: any) {
        console.log('Error firing meta pixel convert event');
        console.log(conversionPayload);
        if (error.response) {
            // The request was made and the server responded with a status code
            // that falls out of the range of  2xx
            console.log(error.response.data);
            console.log(error.response.status);
            console.log(error.response.headers);
        } else if (error.request) {
            // The request was made but no response was received
            console.log(error.request);
        } else {
            // Something happened in setting up the request that triggered an Error
            console.log('Error', error.message);
        }
    }
}

export function categorizeSubscriptions(
    subscriptions: SubscriptionListItem[],
): SubscriptionStatusCategories {
    return subscriptions.reduce(
        (acc: SubscriptionStatusCategories, subscription) => {
            if (subscription.status === 'active') {
                acc['active'] = acc['active'] || [];
                acc['active'].push(subscription);
            } else if (subscription.status === 'paused') {
                acc['paused'] = acc['paused'] || [];
                acc['paused'].push(subscription);
            } else if (subscription.status === 'canceled') {
                acc['canceled'] = acc['canceled'] || [];
                acc['canceled'].push(subscription);
            } else if (subscription.status === 'scheduled-cancel') {
                acc['scheduled-cancel'] = acc['scheduled-cancel'] || [];
                acc['scheduled-cancel'].push(subscription);
            } else {
                acc['unknown'] = acc['unknown'] || [];
                acc['unknown'].push(subscription);
            }
            return acc;
        },
        {
            active: [],
            paused: [],
            canceled: [],
            unknown: [],
            'scheduled-cancel': [],
        },
    );
}

export function getAllProductHrefs(
    categories: SubscriptionStatusCategories,
): string[] {
    const allHrefs: string[] = [];

    // Iterate over each category (active, paused, canceled)
    Object.values(categories).forEach((category: SubscriptionListItem[]) => {
        // Iterate over each item in the category
        category.forEach((item: SubscriptionListItem) => {
            // Push the product_href to the allHrefs array
            allHrefs.push(item.product_href);
        });
    });

    // Use Set to keep only unique hrefs and convert it back to an array
    const uniqueHrefs: string[] = Array.from(new Set(allHrefs));

    return uniqueHrefs;
}

export function categorizeOrders(orders: OrderItem[]): OrderStatusCategories {
    return orders.reduce(
        (acc: OrderStatusCategories, order) => {
            if (order.shipping_status === ShippingStatus.Delivered) {
                acc['delivered'] = acc['delivered'] || [];
                acc['delivered'].push(order);
            } else if (order.shipping_status === ShippingStatus.Shipped) {
                acc['shipped'] = acc['shipped'] || [];
                acc['shipped'].push(order);
            } else if (
                order.order_status !== RenewalOrderStatus.PharmacyProcessing &&
                order.dosage_selection_completed
            ) {
                acc['processing'] = acc['processing'] || [];
                acc['processing'].push(order);
            } else if (
                order.order_status === OrderStatus.UnapprovedCardDown ||
                order.order_status ===
                    RenewalOrderStatus.CheckupComplete_Unprescribed_Paid ||
                // order.order_status ===
                //     RenewalOrderStatus.CheckupComplete_Unprescribed_Unpaid ||
                order.order_status ===
                    RenewalOrderStatus.CheckupIncomplete_Unprescribed_Paid ||
                order.order_status ===
                    RenewalOrderStatus.CheckupIncomplete_Unprescribed_Paid_1 ||
                order.order_status ===
                    RenewalOrderStatus.CheckupIncomplete_Unprescribed_Paid_2 ||
                order.order_status ===
                    RenewalOrderStatus.CheckupWaived_Unprescribed_Paid
            ) {
                acc['review'] = acc['review'] || [];
                acc['review'].push(order);
            } else if (
                order.order_status === OrderStatus.Canceled ||
                order.order_status === OrderStatus.AdministrativeCancel ||
                order.order_status === OrderStatus.DeniedCardDown ||
                order.order_status === RenewalOrderStatus.Denied_Paid ||
                order.order_status === RenewalOrderStatus.Denied_Unpaid
            ) {
                acc['canceled'] = acc['canceled'] || [];
                acc['canceled'].push(order);
            } else if (
                order.order_status === OrderStatus.ApprovedCardDown ||
                order.order_status === OrderStatus.PaymentCompleted ||
                order.order_status === OrderStatus.ApprovedCardDownFinalized ||
                order.order_status === RenewalOrderStatus.PharmacyProcessing ||
                order.order_status ===
                    RenewalOrderStatus.CheckupComplete_Prescribed_Paid
            ) {
                acc['pharmacy-processing'] = acc['pharmacy-processing'] || [];
                acc['pharmacy-processing'].push(order);
            } else if (order.order_status === OrderStatus.PaymentDeclined) {
                acc['payment-failed'] = acc['payment-failed'] || [];
                acc['payment-failed'].push(order);
            } else if (
                order.order_status === RenewalOrderStatus.Unknown ||
                order.order_status ===
                    RenewalOrderStatus.Scheduled_Admin_Cancel ||
                order.order_status === RenewalOrderStatus.Scheduled_Cancel ||
                order.order_status === RenewalOrderStatus.Incomplete
            ) {
                // do nothing
            } else {
                // acc['review'] = acc['review'] || [];
                // acc['review'].push(order);
            }
            return acc;
        },
        {
            delivered: [],
            'pharmacy-processing': [],
            review: [],
            shipped: [],
            canceled: [],
            'payment-failed': [],
            processing: [],
        },
    );
}

export async function mergeOrders(
    orders: OrderItem[],
    renewalOrders: OrderItem[],
): Promise<OrderItem[]> {
    // Step 1: Concatenate the arrays
    const combinedOrders: OrderItem[] = [...orders, ...renewalOrders];

    // Step 2: Add the order_type field to each item
    await Promise.all(
        combinedOrders.map(async (order) => {
            if (orders.includes(order)) {
                order.order_type = OrderType.Order;
            } else if (renewalOrders.includes(order)) {
                order.order_type = OrderType.RenewalOrder;
            }
            var price;
            if (order.order_type === OrderType.RenewalOrder) {
                price = await getPriceForRenewalOrder(order.renewal_order_id!);
            } else {
                price = (await getPriceForProduct(order.id)) || 0;
            }

            order.price = price;
        }),
    );

    // Step 3: Sort the combined array by the created_at field
    combinedOrders.sort(
        (a, b) =>
            new Date(b.created_at).getTime() - new Date(a.created_at).getTime(),
    );

    return combinedOrders;
}

export function formatSubscriptionType(subscriptionType: string) {
    switch (subscriptionType) {
        case 'monthly':
            return 'Monthly';
        case 'quarterly':
            return 'Quarterly';
        case 'one_time':
            return 'One-Time';
        default:
            return 'N/A';
    }
}

export async function convertBundleVariantToSingleVialScript(
    order_data: DBOrderData,
    patient_data: DBPatientData,
    order_type: OrderType,
    variant_index: number
) {
    const new_variant_index = convertBundleVariantToSingleVariant(
        order_data.product_href,
        variant_index
    );

    const pharmacy = getEligiblePharmacy(
        order_data.product_href,
        new_variant_index
    );

    if (pharmacy === PHARMACY.EMPOWER) {
        const bmi_answers = await getQuestionAnswersForBMI(patient_data.id);

        const result = generateEmpowerScript(
            patient_data,
            order_data,
            order_type,
            bmi_answers,
            new_variant_index
        );
        if (result) {
            const { script, sigs, displayName } = result;

            return script;
        }
    }

    if (pharmacy === PHARMACY.HALLANDALE) {
        const addressData: AddressInterface = {
            address_line1:
                order_type === OrderType.Order
                    ? order_data.address_line1
                    : order_data.order.address_line1,
            address_line2:
                order_type === OrderType.Order
                    ? order_data.address_line2
                    : order_data.order.address_line2,
            city:
                order_type === OrderType.Order
                    ? order_data.city
                    : order_data.order.city,
            state:
                order_type === OrderType.Order
                    ? order_data.state
                    : order_data.order.state,
            zip:
                order_type === OrderType.Order
                    ? order_data.zip
                    : order_data.order.zip,
        };

        const result = generateHallandaleScript(
            patient_data,
            order_data,
            addressData,
            order_type,
            new_variant_index
        );
        if (result) {
            const { script, sigs, displayName } = result;

            return script;
        }
    }
}

export async function getScriptForVariantIndex(
    order_data: DBOrderData,
    patient_data: DBPatientData,
    orderType: OrderType,
    incoming_variant_index: number
) {
    let pharmacy = getEligiblePharmacy(
        order_data.product_href,
        incoming_variant_index
    );
    let variant_index = incoming_variant_index;

    if (order_data.product_href === PRODUCT_HREF.TIRZEPATIDE) {
        const mappedIndex =
            HALLANDALE_EMPOWER_CONVERSION_MAP['tirzepatide'][
                incoming_variant_index as keyof (typeof HALLANDALE_EMPOWER_CONVERSION_MAP)['tirzepatide']
            ];
        if (mappedIndex !== variant_index) {
            variant_index = mappedIndex;
            order_data.assigned_pharmacy = 'empower';
            pharmacy = 'empower';
            order_data.variant_index = mappedIndex;
        }
    }

    if (pharmacy === PHARMACY.EMPOWER) {
        const bmi_answers = await getQuestionAnswersForBMI(patient_data.id);

        const result = generateEmpowerScript(
            patient_data,
            order_data,
            orderType,
            bmi_answers,
            variant_index
        );
        if (result) {
            const { script, sigs, displayName } = result;

            return { script, pharmacy };
        }
    } else if (pharmacy === PHARMACY.HALLANDALE) {
        const result = await generateHallandaleScriptWithPDF(
            patient_data.id,
            orderType === OrderType.Order
                ? order_data.id
                : order_data.renewal_order_id,
            variant_index,
            patient_data
        );
        return { script: result, pharmacy };
    }
    return { script: null, pharmacy: null };
}

export async function generateHallandaleScriptWithPDF(
    user_id: string,
    renewal_order_id: string,
    variant_index: number,
    patientData: DBPatientData
) {
    const { data: allergyData, error: allergyError } =
        await getPatientAllergyData(user_id, 'deprecated');

    const { data: renewalOrder, type: orderType } = await fetchOrderData(
        renewal_order_id
    );

    if (patientData && renewalOrder && allergyData) {
        const addressData: AddressInterface = {
            address_line1: renewalOrder.address_line1,
            address_line2: renewalOrder.address_line2,
            city: renewalOrder.city,
            state: renewalOrder.state,
            zip: renewalOrder.zip,
        };
        const scriptMetadata = generateHallandaleScript(
            patientData,
            renewalOrder,
            addressData,
            OrderType.RenewalOrder,
            variant_index
        );

        if (scriptMetadata) {
            const base64pdf = convertHallandaleOrderToBase64(
                scriptMetadata.script,
                allergyData && allergyData.length > 0
                    ? allergyData[0].allergies
                    : 'nkda'
            );

            const orderWithPdf: HallandaleOrderObject = {
                ...scriptMetadata.script,
                document: { pdfBase64: base64pdf },
            };

            const body_json: HallandaleScriptJSON = {
                message: {
                    id: renewalOrder.id,
                    sentTime: new Date().toISOString(),
                },
                order: orderWithPdf,
            };
            return body_json;
        }
    }
    return null;
}

export function convertBundleVariantToSingleVariant(
    product: string,
    bundle_variant_index: number
): number {
    if (
        !shouldConvertBundleSubscriptionToMonthly(
            product as PRODUCT_HREF,
            bundle_variant_index
        )
    ) {
        return bundle_variant_index;
    }
    switch (product) {
        case 'semaglutide':
            return SEMAGLUTIDE_BUNDLE_MONTHLY_MAPPING[bundle_variant_index];
        case 'tirzepatide':
            return TIRZEPATIDE_BUNDLE_MONTHLY_MAPPING[bundle_variant_index];
    }

    return -1;
}

export function shouldConvertBundleSubscriptionToMonthly(
    product_href: PRODUCT_HREF,
    variant_index: number
) {
    if (product_href === PRODUCT_HREF.SEMAGLUTIDE) {
        if (SEMAGLUTIDE_NO_CONVERT_VARIANTS.includes(variant_index)) {
            return false;
        }
        return true;
    }

    if (product_href === PRODUCT_HREF.TIRZEPATIDE) {
        if (TIRZEPATIDE_NO_CONVERT_VARIANTS.includes(variant_index)) {
            return false;
        }

        return true;
    }
}

export async function generateBoothwynScriptAsync(
    patient_id: string,
    order_id: string,
    override?: ScriptOverrideObject,
    resend?: boolean
): Promise<BoothwynScriptPayload> {
    const { data: patient_data_any, error } = await getPatientInformationById(
        patient_id
    );

    const { type: order_type, data: order_data_any } = await fetchOrderData(
        order_id
    );

    if (!patient_data_any) {
        return {
            script_json: null,
            error: 'No patient was found with provided Patient ID',
        };
    }

    const resendCount = await getResendCount(
        parseInt(order_id),
        order_type === OrderType.Order
            ? undefined
            : order_data_any.renewal_order_id
    );

    const patient_data: DBPatientData = patient_data_any;
    const order_data: DBOrderData = order_data_any;
    const product_href: string = override
        ? override.product_href
        : order_data.product_href;
    const variant_index = override
        ? override.variant_index
        : order_data.variant_index;

    if (!BOOTHWYN_ALLOWED_PRODUCT_MAP[product_href].includes(variant_index)) {
        return {
            script_json: null,
            error: `Product - Variant Mapping is not for Boothwyn: ${variant_index} : ${product_href}`,
        };
    }

    const prescription_object =
        BOOTHWYN_VARIANT_MAP[product_href][variant_index];

    const isBoothwynQuarterly =
        BOOTHWYN_QUARTERLY_PROGRAM_VARIANTS[product_href].includes(
            variant_index
        );

    const script: BoothwynScriptJSON = {
        caseId: `BIOVERSE-${
            order_type === OrderType.RenewalOrder
                ? order_data.renewal_order_id
                : order_data.id
        }${resend ? 'R'.repeat(resendCount + 1) : ''}`,
        orderType: 2,
        shippingMethod: 26,
        ...(isBoothwynQuarterly ? { program: 'Quarterly Bulk' } : {}),
        patient: {
            firstName: patient_data.first_name,
            lastName: patient_data.last_name,
            email: patient_data.email,
            phoneNumber: patient_data.phone_number,
            dateOfBirth: patient_data.date_of_birth,
            gender: patient_data.sex_at_birth?.charAt(0) || 'U',
            address: {
                address1:
                    order_data.address_line1 ?? patient_data.address_line1,
                ...(order_data.address_line2 || patient_data.address_line2
                    ? {
                          address2:
                              order_data.address_line2 ??
                              patient_data.address_line2,
                      }
                    : {}),
                city: order_data.city ?? patient_data.city,
                state: order_data.state ?? patient_data.state,
                zipCode: order_data.zip ?? patient_data.zip,
            },
        },

        clinician: boothwynProviderObject,
        prescriptions: prescription_object,
    };

    return {
        script_json: script,
        error: null,
    };
}

