        // Create a deep copy to avoid mutating state directly
        const newForm = prescriptionForm;

        // Update the prescription_items of the first prescription
        newForm.prescriptions[0].prescription_items = [newPrescriptionItem];

        return newForm;
    };

    const addSecondaryItemWithSpecification = (
        prescriptionForm: TMCPrescriptionForm,
        id: string,
        quantity: number = 1,
        sig: string
    ): void => {
        // Note the return type is now void since we're mutating the input directly
        // Check if there is an item at index [1] and remove it if it exists
        if (prescriptionForm.prescriptions[0].prescription_items.length > 1) {
            prescriptionForm.prescriptions[0].prescription_items.splice(1, 1);
        }

        // Add a new item to the prescription_items array of the first prescription
        prescriptionForm.prescriptions[0].prescription_items.push({
            Id: id,
            Quantity: String(quantity),
            NoOfOriginalRefills: '0',
            NoOfRefillRemaining: '0',
            Sig: sig,
        });

        // Since we're modifying the input directly, no need to return anything
    };

    switch (orderData.product_href) {
        case 'b12-injection':
            const newPrescriptionItem = {
                Id: medication_object!.id,
                Quantity: orderData.subscription_type === 'monthly' ? '1' : '3',
                NoOfOriginalRefills: '0',
                NoOfRefillRemaining: '0',
                Sig: medication_object!.sig,
                Reason_for_Compounding: 'Product not available commercially',
            };

            console.log('treated as b12');

            updatePrescriptionForm(prescriptionForm, newPrescriptionItem);

            break;
        case 'nad-injection':
        case 'glutathione-injection':
            const newPrescriptionItemNonB12 = {
                Id: medication_object!.id,
                Quantity: '1',
                NoOfOriginalRefills: '0',
                NoOfRefillRemaining: '0',
                Sig: medication_object!.sig,
                Reason_for_Compounding: 'Product not available commercially',
            };

            updatePrescriptionForm(prescriptionForm, newPrescriptionItemNonB12);

            addSecondaryItemWithSpecification(
                prescriptionForm,
                '01t1R000007FvGmQAK',
                2,
                medication_object!.sig
            );

            break;
    }

    return prescriptionForm;
}

constructPricingStructure = (
    productData: any,
    priceData: ProductVariantRecord[],
    shouldDiscount: boolean,
) => {
    // Construct { full_price, subscribesave_price, coupon_price, total_price }
    const subscriptionType = productData['subscriptionType'];
    const variant = productData['variant'];
    const productName = productData['productName'];
    const product = priceData[variant].price_data;

    if (productName === CGM_SENSOR_PRODUCT_HREF) {
        if (subscriptionType === 'monthly') {
            const total_price = 195 - (shouldDiscount ? 20 : 0);
            return {
                item_price: '238.00',
                subscribe_save_price: '43.00',
                total_price: total_price.toFixed(2),
                ...(shouldDiscount && {
                    coupon_price: parseFloat(
                        product.discount_price.discount_amount,
                    ).toFixed(2),
                }),
            };
        }
    }

    let res: any = {};
    var full_price = parseFloat(product.product_price);
    res['item_price'] = full_price;

    // Determine if should apply Subscribe + Save (aka only monthly products)
    if (subscriptionType !== 'quarterly' && subscriptionType !== 'one_time') {
        // Apply Subscribe & Save

        const oneTimePriceRecord = priceData.find(
            (record) => record.cadence === 'one_time',
        );

        if (oneTimePriceRecord) {
            const one_time_price = parseFloat(
                oneTimePriceRecord.price_data.product_price,
            );

            const subscribe_save_price = product.product_price - one_time_price;
            res['subscribe_save_price'] = Math.abs(subscribe_save_price);
        }
    }

    // Determine coupon savings
    // Dont apply coupon savings if it's one-time
    if (
        (subscriptionType !== 'one_time' && shouldDiscount) ||
        isWeightlossProduct(productData.productName)
    ) {
        const coupon_price = parseFloat(product.discount_price.discount_amount);
        res['coupon_price'] = coupon_price;
    }

    const total =
        full_price -
        (res['subscribe_save_price'] ?? 0) -
        (res['coupon_price'] ?? 0);
    res['total_price'] = total;

    return Object.keys(res).reduce((acc: any, key: any) => {
        // Convert each value to a string with two decimal places and then to a number
        acc[key] = res[key].toFixed(2);
        return acc;
    }, {});
}

